code.cpp:
#include <iostream>
#include <string>
#include <vector>
using namespace std;

class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        int count = 0;
        int windowStart = 0;
        vector<int> freq(3, 0); // index 0->'a', 1->'b', 2->'c'

        for (int windowEnd = 0; windowEnd < n; windowEnd++) {
            freq[s[windowEnd] - 'a']++;

            // while window contains at least one 'a','b','c'
            while (freq[0] > 0 && freq[1] > 0 && freq[2] > 0) {
                count += (n - windowEnd); // all substrings ending at or after windowEnd
                freq[s[windowStart] - 'a']--;
                windowStart++;
            }
        }
        return count;
    }
};

int main() {
    Solution sol;
    string s1 = "abcba";
    cout << "Number of valid substrings: " << sol.numberOfSubstrings(s1) << endl; // 5

    string s2 = "ccabcc";
    cout << "Number of valid substrings: " << sol.numberOfSubstrings(s2) << endl; // 8
    return 0;
}
