#include <iostream>
#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int numSubarraysWithSum(vector<int>& nums, int goal) {
        unordered_map<int, int> prefixCount;
        prefixCount[0] = 1; // base case
        int prefixSum = 0, result = 0;

        for (int num : nums) {
            prefixSum += num;
            if (prefixCount.find(prefixSum - goal) != prefixCount.end()) {
                result += prefixCount[prefixSum - goal];
            }
            prefixCount[prefixSum]++;
        }
        return result;
    }
};

int main() {
    Solution sol;
    vector<int> nums1 = {1, 1, 0, 1, 0, 0, 1};
    cout << "Number of subarrays with sum 3: " << sol.numSubarraysWithSum(nums1, 3) << endl; // 4

    vector<int> nums2 = {0, 0, 0, 0, 1};
    cout << "Number of subarrays with sum 0: " << sol.numSubarraysWithSum(nums2, 0) << endl; // 10
    return 0;
}
