#include <iostream>
#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int numberOfSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> prefixCount;
        prefixCount[0] = 1;
        int prefixSum = 0, result = 0;

        for (int num : nums) {
            prefixSum += (num % 2); // count odd as 1, even as 0
            if (prefixCount.find(prefixSum - k) != prefixCount.end()) {
                result += prefixCount[prefixSum - k];
            }
            prefixCount[prefixSum]++;
        }
        return result;
    }
};

int main() {
    Solution sol;
    vector<int> nums1 = {1, 1, 2, 1, 1};
    cout << "Number of nice subarrays: " << sol.numberOfSubarrays(nums1, 3) << endl; // 2

    vector<int> nums2 = {4, 8, 2};
    cout << "Number of nice subarrays: " << sol.numberOfSubarrays(nums2, 1) << endl; // 0
    return 0;
}
