code.javascript:
function characterReplacement(s, k) {
    let windowStart = 0;
    let maxLength = 0;
    let charFrequency = {};
    let maxRepeatLetterCount = 0;

    for (let windowEnd = 0; windowEnd < s.length; windowEnd++) {
        // Add current character to frequency map
        charFrequency[s[windowEnd]] = (charFrequency[s[windowEnd]] || 0) + 1;

        // Update max repeat letter count
        maxRepeatLetterCount = Math.max(maxRepeatLetterCount, charFrequency[s[windowEnd]]);

        // Check if current window is valid
        // We need to replace (currentLength - maxRepeatLetterCount) characters
        if ((windowEnd - windowStart + 1) - maxRepeatLetterCount > k) {
            // Window is invalid, contract from left
            charFrequency[s[windowStart]]--;
            windowStart++;
        }

        // Update maxLength
        maxLength = Math.max(maxLength, windowEnd - windowStart + 1);
    }

    return maxLength;
}
