code.cpp:
#include <iostream>
#include <vector>
#include <queue>
using namespace std;

class Solution {
public:
    vector<int> firstNegativeInWindow(vector<int>& arr, int k) {
        vector<int> result;
        queue<int> negativeQueue;
        int windowStart = 0;

        for (int windowEnd = 0; windowEnd < arr.size(); windowEnd++) {
            // Add negative numbers to queue
            if (arr[windowEnd] < 0) {
                negativeQueue.push(windowEnd);
            }

            // Check if window size is k
            if (windowEnd >= k - 1) {
                // Find first negative in current window
                if (negativeQueue.empty()) {
                    result.push_back(0);
                } else {
                    result.push_back(arr[negativeQueue.front()]);
                }

                // Remove element that's going out of window
                if (arr[windowStart] < 0) {
                    negativeQueue.pop();
                }
                windowStart++;
            }
        }

        return result;
    }
};

int main() {
    Solution sol;
    vector<int> arr1 = {-8, 2, 3, -6, 10};
    vector<int> arr2 = {12, -1, -7, 8, -15, 30, 16, 28};

    vector<int> result1 = sol.firstNegativeInWindow(arr1, 2);
    vector<int> result2 = sol.firstNegativeInWindow(arr2, 3);

    cout << "Result 1: ";
    for (int num : result1) cout << num << " ";
    cout << endl;

    cout << "Result 2: ";
    for (int num : result2) cout << num << " ";
    cout << endl;

    return 0;
}
