#include <iostream>
#include <string>
#include <unordered_map>
#include <algorithm>
using namespace std;

class Solution {
public:
    int lengthOfLongestSubstringKDistinct(string s, int k) {
        if (k == 0) return 0;

        // INTUITION: Use sliding window + hash map for constraint tracking
        int windowStart = 0;
        int maxLength = 0;
        unordered_map<char, int> charMap;  // Track character frequencies

        // INTUITION: Expand window with right pointer
        for (int windowEnd = 0; windowEnd < s.length(); windowEnd++) {
            // Add current character to hash map
            charMap[s[windowEnd]]++;

            // INTUITION: Shrink window while constraint violated
            while (charMap.size() > k) {
                // Remove leftmost character
                charMap[s[windowStart]]--;
                if (charMap[s[windowStart]] == 0) {
                    charMap.erase(s[windowStart]);  // Remove key if frequency = 0
                }
                windowStart++;
            }

            // INTUITION: Update maximum length with current valid window
            maxLength = max(maxLength, windowEnd - windowStart + 1);
        }

        // INTUITION: Return longest valid substring length
        return maxLength;
    }
};

int main() {
    Solution sol;
    // Example: "araaci" with k=2
    // Expected: 4 (substring "araa")
    cout << sol.lengthOfLongestSubstringKDistinct("araaci", 2) << endl;
    cout << sol.lengthOfLongestSubstringKDistinct("araaci", 1) << endl;
    cout << sol.lengthOfLongestSubstringKDistinct("cbbebi", 3) << endl;
    return 0;
}
