#include <iostream>
#include <unordered_map>
#include <vector>
using namespace std;

class Solution {
public:
    int findLength(const vector<char> &arr) {
        int windowStart = 0, maxLength = 0;
        unordered_map<char, int> fruitFrequencyMap;

        for (int windowEnd = 0; windowEnd < arr.size(); ++windowEnd) {
            fruitFrequencyMap[arr[windowEnd]]++;

            while (fruitFrequencyMap.size() > 2) {
                fruitFrequencyMap[arr[windowStart]]--;
                if (fruitFrequencyMap[arr[windowStart]] == 0) {
                    fruitFrequencyMap.erase(arr[windowStart]);
                }
                windowStart++;
            }

            maxLength = max(maxLength, windowEnd - windowStart + 1);
        }
        return maxLength;
    }
};

int main() {
    Solution sol;
    cout << "Maximum number of fruits: " 
         << sol.findLength(vector<char>{'A', 'B', 'C', 'A', 'C'}) << endl; // 3
    cout << "Maximum number of fruits: " 
         << sol.findLength(vector<char>{'A', 'B', 'C', 'B', 'B', 'C'}) << endl; // 5
    return 0;
}
