code.cpp:
#include <iostream>
#include <string>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int characterReplacement(string s, int k) {
        int windowStart = 0, maxLength = 0, maxRepeatLetterCount = 0;
        unordered_map<char, int> charFrequency;

        for (int windowEnd = 0; windowEnd < s.length(); ++windowEnd) {
            charFrequency[s[windowEnd]]++;
            maxRepeatLetterCount = max(maxRepeatLetterCount, charFrequency[s[windowEnd]]);

            // If we need more replacements than allowed, shrink the window
            while ((windowEnd - windowStart + 1 - maxRepeatLetterCount) > k) {
                charFrequency[s[windowStart]]--;
                windowStart++;
            }

            maxLength = max(maxLength, windowEnd - windowStart + 1);
        }
        return maxLength;
    }
};

int main() {
    Solution sol;
    string s1 = "BAABAABBBAAA";
    cout << "Longest substring length: " << sol.characterReplacement(s1, 2) << endl; // 6

    string s2 = "AABABBA";
    cout << "Longest substring length: " << sol.characterReplacement(s2, 1) << endl; // 4
    return 0;
}
